	/*
		写一个包 , 暴露出去
	*/
	var fs=require('fs');

	var rf = function(path,callback){

		// 第一个参数是路径,第二个参数考虑 liunx 系统 , window不考虑 ,第三个返回布尔值
		fs.access(path,(err)=>{

			if(err){
				// throw err;
				callback(err);
			}else {
				fs.readFile(path, 'utf8', (err,data)=>{
					// 执行回调函数
					callback(null,data);
				});
			}
		});

	}
	

	module.exports = rf;